function handleSurveyFormSubmit(e){e.preventDefault();const a=new FormData(surveyForm).entries(),{email:t,password:r}=Object.fromEntries(a),s=validateEmail(t),i=validatePassword(r);if(!s){document.querySelector(".email .form-field__messages").innerText=s}if(i){document.querySelector(".password .form-field__messages").innerText=i}}function validatePassword(e,a){if(!e)return"Password is required";if(e.length<a)return`Please enter a password that's at least ${a} characters long`;if(!/[A-Z]/g.test(e))return"Please use at least one capital letter.";return/\d/g.test(e)?"":"Please use at least one number."}function validateEmail(e){if(!e)return"Email is required";return/^\S+@\S+$/g.test(e)?"":"Please enter a valid email"}handleSurveyFormSubmit(),validatePassword(),validateEmail();